INSERT INTO `textes` (`id`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `meta.adresse`, `geo.type`, `geo.coordinates`, `geoposition`, `nom`) VALUES ('25', 'Albert Robida', 'https://gallica.bnf.fr/ark:/12148/bpt6k6557894s/f21.image.r=ex%20bois', '', '" [...]  tout à fait au-dessous de la nacelle, comme un damier, les rues et les places poudreuses de l''ex-bois de Boulogne se dessinaient en carrés réguliers couverts d''usines et de cités ouvrières, dont les jardinets formaient tout ce que le temps avait respecté de l''ancienne promenade des élégants des siècles derniers".', '', 'Bois de Boulogne', 'Point', '[-1.316796, 46.799387]', POINT(-1.316796, 46.799387), 'Le Vingtième Siècle'),
('26', 'Albert Robida', 'https://gallica.bnf.fr/ark:/12148/bpt6k6557894s/f60.image.r=place%20de%20l', '', 'La place de l''Étoile, couverte entièrement, a été convertie en jardin d''hiver. Au-dessus, c''est-à-dire directement sur l''Arc des batailles, un immense palais s''est élevé, portant à des hauteurs inusitées ses pavillons et ses tours.', '', 'Place de l''Etoile', 'Point', '[1.534434, 48.648441]', POINT(1.534434, 48.648441), 'Le Vingtième Siècle'),
('27', 'Albert Robida', 'https://gallica.bnf.fr/ark:/12148/bpt6k6557894s/f60.image.r=jardin%20suspendu', '', 'De l''Arc de triomphe au Trocadéro court, sur des piliers, un superbe jardin suspendu, un parc aérien réservé aux voyageurs de l''hôtel et aux habitants de l''édifice encore plus aérien que nous allons décrire, car les architectes ne se sont pas contentés de la construction du gigantesque hôtel qui, jusqu''aux premières nuées, porte des coupoles et des tours.', '', 'trocadéro', 'Point', '[2.28787, 48.863905]', POINT(2.28787, 48.863905), 'Le Vingtième Siècle');
