INSERT INTO `textes` (`id`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `meta.adresse`, `geo.type`, `geo.coordinates`, `geoposition`, `nom`) VALUES ('40', 'Alfred de Musset', 'https://www.bonjourpoesie.fr/lesgrandsclassiques/poemes/alfred_de_musset/une_promenade_au_jardin_des_plantes', '', 'Sous ces arbres chéris, où j''allais à mon tourPour cueillir, en passant, seul, un brin de verveine,Sous ces arbres charmants où votre fraîche haleineDisputait au printemps tous les parfums du jour ;Des enfants étaient là qui jouaient alentour ;Et moi, pensant à vous, j''allais traînant ma peine ;Et si de mon chagrin vous êtes incertaineVous ne pouvez pas l''être au moins de mon amour.Mais qui saura jamais le mal qui me tourmente ?Les fleurs des bois, dit-on, jadis ont deviné !Antilope aux yeux noirs, dis, quelle est mon amante ?Ô lion, tu le sais, toi, mon noble enchaîné ;Toi qui m''as vu pâlir lorsque sa main charmanteSe baissa doucement sur ton front incliné.', '', 'Jardin des Plantes, 57 Rue Cuvier, 75005 Paris', 'Point', '[2.363925, 48.844468]', POINT(2.363925, 48.844468), '"Promenade au Jardin des Plantes", sonnet de 1852, Monde illustré, 9 mai 1857.');
