INSERT INTO `textes` (`id`, `nom`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `geo`) VALUES ('29', 'Notice des produits de l''industrie française, précédée d''un historique des expositions antérieures et d''un coup d''oeil général sur l''Exposition actuelle', 'Exposition publique des produits de l''industrie française', 'https://www.google.fr/books/edition/Notice_des_produits_de_l_industrie_fran/Ue-FpgBOjtIC?hl=fr&gbpv=1', '', '"Mlle Faure (Emilie), fleuriste, rue Saint-Pierre-Montmartre, n° 15. Cette demoiselle arrive du Brésil, et elle a rapporté de ce pays une immense quantité de plumes d''oiseaux, qu''elle transforme en fleurs de toutes espèces. Ce sont donc des plumes d''oiseaux que le public a sous les yeux ; qu''il juge si la vérité d''imitation peut aller plus loin. Mlle Faure fabrique aussi les fleurs en cire".', '', '{''type'': ''Point'', ''coordinates'': [1.251504, 49.552903]}');
