INSERT INTO `textes` (`id`, `nom`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `geo`) VALUES ('37', 'Le carnaval des animaux', 'Francis Blanche', 'http://musique.ac-dijon.fr/auditor/2004/stsaens/carnaval.pdf', '', '"Au jardin des plantes,Ainsi nommé d''ailleursA cause des animauxQu''on y a rassemblés,Au jardin des plantes,Une étrange ardeur semble régner.On décore. On festonne.On visse. On cloue. On plante."Le castor construit des tréteaux.La grue porte des fardeaux.Le python accroche des tableaux.Car ce soir, au Jardin des plantes,C''est la Grand-fête éblouissante :Le Carnaval des Animaux.', '', '{''type'': ''Point'', ''coordinates'': [2.363922, 48.844465]}');
