INSERT INTO `textes` (`id`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `meta.adresse`, `geo.type`, `geo.coordinates`, `geoposition`, `nom`) VALUES ('31', 'Gustave Flaubert', 'https://fr.wikisource.org/wiki/Madame_Bovary/Texte_entier', '', '"Sa mère lui choisit une chambre, au quatrième, sur l’Eau-de-Robec, chez un teinturier de sa connaissance. Elle conclut les arrangements pour sa pension, se procura des meubles, une table et deux chaises, fit venir de chez elle un vieux lit en merisier, et acheta de plus un petit poêle en fonte, avec la provision de bois qui devait chauffer son pauvre enfant. Puis elle partit au bout de la semaine, après mille recommandations de se bien conduire, maintenant qu’il allait être abandonné à lui-même".', '', 'rue Eau-de-Robec, Rouen', 'Point', '[2.483566, 49.028335]', POINT(2.483566, 49.028335), 'Madame Bovary');
