INSERT INTO `textes` (`id`, `nom`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `geo`) VALUES ('18', 'Nuits d''octobre', 'Gérard de Nerval', 'https://fr.wikisource.org/wiki/%C5%92uvres_compl%C3%A8tes_de_G%C3%A9rard_de_Nerval_-_Tome_V/Promenades_et_Souvenirs', '', 'Ce qui me séduisait dans ce petit espace abrité par les grands arbres du Château des Brouillards, c’était d’abord ce reste de vignoble lié au souvenir de saint Denis, qui, au point de vue des philosophes, était peut-être le second Bacchus, Διονύσιος, et qui a eu trois corps dont l’un a été enterré à Montmartre, le second à Ratisbonne et le troisième à Corinthe.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c0/Nerval_-_Le_R%C3%AAve_et_la_Vie%2C_L%C3%A9vy%2C_1868.djvu/page11-240px-Nerval_-_Le_R%C3%AAve_et_la_Vie%2C_L%C3%A9vy%2C_1868.djvu.jpg', '{''type'': ''Point'', ''coordinates'': [2.337551, 48.888429]}');
