INSERT INTO `textes` (`id`, `nom`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `geo`) VALUES ('36', 'L''été à Paris', 'Jules Janin', 'https://gallica.bnf.fr/ark:/12148/bpt6k9650048g.r=%22Jardin%20des%20Plantes%22Paris?rk=21459;2', '', 'Le seul Jardin-des-Plantes, cette oasis perdue dans les ténèbres, ce lieu de rafraichissement et de repos placé au sommet de cette montagne admirable, suffirait à composer tout un gros tome. Le monde entier est contenu dans cette vaste enceinte. Prêtez l''oreille, vous entendrez chanter tous les oiseaux de l''air, vous entendrez mugir toutes les bêtes des forêts : le lion et l''oiseau-mouche, la girafe et le chat sauvage, toute la famille des singes ; toutes les plantes du midi et du nord ; tout ce qui vit, tout ce qui a vécu [...]', '', '{''type'': ''Point'', ''coordinates'': [2.363921, 48.844464]}');
