INSERT INTO `textes` (`id`, `info.author`, `info.source`, `info.category`, `info.contenu`, `meta.img`, `meta.adresse`, `geo.type`, `geo.coordinates`, `geoposition`, `nom`) VALUES ('22', 'Théophile Gautier', 'https://gallica.bnf.fr/ark:/12148/bpt6k102024x/f322.item', '', '"....occupons-nous maintenant du chef, élu de la nation.- Nous l''établissons sur la butte Montmartre, que l''on ferait écrouler sous d''énormes pressions, et qui, tassée de la sorte, servira merveilleusement aux remblais, aux terrasses et aux travaux de substruction. Les serres, les orangeries, les écuries, toutes les dépendances occuperont le premier gradin de cette pyramide de constructions, dont les assises inférieures commenceront à l''endroit où est aujourd''hui l''église de Notre-Dame-de-Lorette".', '', 'La butte Montmartre', 'Point', '[2.334612, 48.885539]', POINT(2.334612, 48.885539), 'Paris futur'),
('23', 'Théophile Gautier', 'https://gallica.bnf.fr/ark:/12148/bpt6k102024x/f322.item', '', '"Chaque maison serait tenue d''avoir une bouche de son calorifère tournée à l''extérieur, de sorte que, dans ces passages, ainsi fermés, l''on jouirait de la plus douce température ; que de rhumes, que de fluxions de poitrine ainsi évités! Dans les quartiers opulents, ces corridors ou ces cloîtres, comme on voudra bien les appeler, seraient garnis de tapis, d''orangers, de magnolias, de lauriers, de camellias et autres arbustes en fleur" (p. 321)', '', 'Pas localisation, mais on peut mettre le texte au niveau de la plaque commémorative où a vécu Théophile Gautier, 8 place des Vosges à Paris', 'Point', '[-52.277794, 4.606287]', POINT(-52.277794, 4.606287), 'Paris futur'),
('24', 'Théophile Gautier', 'https://gallica.bnf.fr/ark:/12148/bpt6k102024x/f322.item', '', '"Grâce aux études faites sur les climatures, on jouira à Paris d''une température assez semblable à celle de Naples. Une large zone de forêts se nouera autour de la ville, comme une ceinture verte, arrêtera les vents et retiendra les brouillards, que ses feuilles absorberont pour les rendre à la terre, qui en fera des sources et des fontaines".', '', 'Avenue Théophile Gautier (pour être plus proche du Bois de Boulogne)', 'Point', '[-1.316796, 46.799387]', POINT(-1.316796, 46.799387), 'Paris futur');
